int gcd ( int a , int b ) { int hcf = 1 , i = 2 , p = 2 , min ; if ( a < b ) min = a ; else min = b ; while ( i <= min ) { if ( a % i == 0 && b % i == 0 ) { hcf *= i ; a /= i ; b /= i ; i = p ; min /= i ; } else { i ++ ; p = i ; } } return hcf ; } int main ( ) { int n ; scanf ( "%d" , & n ) ; int i , g [ n ] , l [ n ] ; for ( i = 0 ; i < n ; i ++ ) { int a , b ; scanf ( "%d%d" , & a , & b ) ; g [ i ] = gcd ( a , b ) ; l [ i ] = a * b / g [ i ] ; } for ( i = 0 ; i < n ; i ++ ) { printf ( "%d %d\n" , g [ i ] , l [ i ] ) ; } return 0 ; }