int main ( ) { int t = 0 ; scanf ( "%d" , & t ) ; while ( t -- ) { long long int sum = 0 , n , m , beg , end , min , i ; scanf ( "%lld" , & n ) ; long long int a [ n ] ; for ( m = 0 ; m < n ; m ++ ) { scanf ( "%lld" , & a [ m ] ) ; } beg = 0 ; end = n - 1 ; min = a [ beg ] ; for ( i = beg + 1 ; i <= end ; i ++ ) { if ( min > a [ i ] ) { min = a [ i ] ; } } printf ( "%lld\n" , min * end ) ; } return 0 ; }