long long int hcf ( int a , int b ) { if ( b == 0 ) return a ; else return hcf ( b , a % b ) ; } int main ( ) { long long int t , a , b , max , min , gcd , lcm ; scanf ( "%lld" , & t ) ; while ( t -- ) { scanf ( "%lld %lld" , & a , & b ) ; max = a > b ? a : b ; min = a <= b ? a : b ; gcd = hcf ( max , min ) ; printf ( "%lld " , gcd ) ; lcm = ( a * b ) / gcd ; printf ( "%lld\n" , lcm ) ; } }