main ( ) { int t ; scanf ( "%d" , & t ) ; for ( int m = 0 ; m < t ; m ++ ) { int n , s = 0 ; scanf ( "%d" , & n ) ; int a [ ( ( n * ( n + 1 ) ) / 2 ) ] ; for ( int l = 0 ; l < n ; l ++ ) { for ( int k = 0 ; k <= l ; k ++ ) scanf ( "%d" , & a [ l + k ] ) ; } int i = 0 ; s = a [ i ] ; int j = 1 ; while ( j < n ) { if ( a [ i + j ] < a [ i + j + 1 ] ) { s = s + a [ i + j + 1 ] ; i = i + j + 1 ; j = j + 1 ; } else { s = s + a [ i + j ] ; i = i + j ; j = j + 1 ; } } printf ( "%d" , s ) ; } }