int gcd ( ) ; void lcm ( ) ; int main ( ) { int T ; long long int a , b , r ; scanf ( "%lld" , & T ) ; while ( T > 0 ) { scanf ( "%lld %lld" , & a , & b ) ; r = gcd ( a , b ) ; printf ( "%lld " , r ) ; lcm ( a , b ) ; T -- ; } return 0 ; } int gcd ( x , y ) { if ( x >= y ) { if ( x % y == 0 ) return y ; else return ( gcd ( x - y , y ) ) ; } else { if ( x % y == 0 ) return y ; else return ( gcd ( x , y - x ) ) ; } } void lcm ( a , b ) { if ( a >= b ) { if ( a % b == 0 ) printf ( "%lld\n" , a ) ; else printf ( "%lld\n" , ( a * b ) ) ; } else { if ( b % a == 0 ) { printf ( "%lld\n" , b ) ; } else printf ( "%lld\n" , ( a * b ) ) ; } }