int main ( ) { int a , b , i = 0 , j = 0 , gcd , lcm , n , k ; scanf ( "%d" , & n ) ; for ( j = 0 ; j < n ; j ++ ) { scanf ( "%d %d" , & a , & b ) ; for ( i = 1 ; i <= a && i <= b ; i ++ ) { if ( a % i == 0 && b % i == 0 && ( a > 0 && b > 0 ) ) { gcd = i ; lcm = ( a * b ) / gcd ; } } if ( a == 0 || b == 0 ) { lcm = 0 ; if ( a == 0 ) { gcd = b ; } else { gcd = a ; } } printf ( "%d %d\n" , gcd , lcm ) ; } return 0 ; }