int main ( ) { int t ; scanf ( "%d" , & t ) ; while ( t -- ) { int i , j , temp ; long long int n , sum ; sum = 0 ; scanf ( "%lld\n" , & n ) ; long long int arr [ n ] ; for ( i = 0 ; i < n ; i ++ ) { scanf ( "%lld" , & arr [ i ] ) ; } for ( i = 0 ; i < n ; i ++ ) { for ( j = 0 ; j < n - i - 1 ; j ++ ) { if ( arr [ j ] > arr [ j + 1 ] ) { temp = arr [ j ] ; arr [ j ] = arr [ j + 1 ] ; arr [ j + 1 ] = temp ; } } } sum = sum + ( arr [ 0 ] * ( n - 1 ) ) ; printf ( "%lld\n" , sum ) ; } return 0 ; }