long long int gcd ( long long int a , long long int b ) { if ( a == b ) { return a ; } if ( a > b ) { return gcd ( a - b , b ) ; } return gcd ( a , b - a ) ; } int main ( ) { long long int t , i , a , b , g , l ; scanf ( "%lld" , & t ) ; for ( i = 0 ; i < t ; i ++ ) { scanf ( "%lld %lld" , & a , & b ) ; g = gcd ( a , b ) ; l = ( a * b ) / g ; printf ( "%lld %lld\n" , g , l ) ; } return 0 ; }