int max , n ; int a [ 10000 ] ; void traverse ( int k , int sum , int c ) { if ( k == n ) { if ( sum > max ) { max = sum ; } return ; } traverse ( k + 1 , sum + a [ c + k ] , c + k ) ; traverse ( k + 1 , sum + a [ c + k + 1 ] , c + k + 1 ) ; } int main ( ) { int res [ 10000 ] ; int i , j , t , l ; scanf ( "%d" , & t ) ; for ( i = 0 ; i < t ; i ++ ) { scanf ( "%d" , & n ) ; for ( j = 0 ; j < n * ( n + 1 ) / 2 ; j ++ ) { scanf ( "%d" , & a [ j ] ) ; } traverse ( 1 , a [ 0 ] , 0 ) ; res [ i ] = max ; } for ( l = 0 ; l < t ; l ++ ) printf ( "%d\n" , res [ l ] ) ; return 0 ; }