int main ( ) { int t , i , n , k , l , m , temp , key ; scanf ( "%d" , & t ) ; for ( i = 1 ; i <= t ; i ++ ) { scanf ( "%d" , & n ) ; int a [ n ] ; for ( k = 0 ; k < n ; k ++ ) { scanf ( "%d" , & a [ k ] ) ; } for ( l = 0 ; l < n - 1 ; l ++ ) { for ( m = 0 ; m < n - l - 1 ; m ++ ) { if ( a [ m ] > a [ m + 1 ] ) { temp = a [ m ] ; a [ m ] = a [ m + 1 ] ; a [ m + 1 ] = temp ; } } } key = a [ 0 ] ; printf ( "%d\n" , key * ( n - 1 ) ) ; } return 0 ; }