int main ( ) { int t ; scanf ( "%d" , & t ) ; while ( t -- ) { int i , j ; int x , a [ i ] [ j ] , p , q , m = 1 , l , o , y , s ; scanf ( "%d" , & x ) ; x = i ; x = j ; for ( p = 1 ; p <= x ; p ++ ) { for ( q = 1 ; q <= m ; q ++ ) { scanf ( "%d" , a [ p ] [ q ] ) ; } printf ( "\n" ) ; m ++ ; } y = a [ 1 ] [ 1 ] ; l = 2 ; o = 1 ; s = o + 1 ; for ( p = 1 ; p <= x ; p ++ ) { if ( a [ l ] [ o ] > a [ l ] [ s ] ) { y = y + a [ l ] [ o ] ; l ++ ; } else { y = y + a [ l ] [ s ] ; l ++ ; o ++ ; } } printf ( "%d\n" , y ) ; } return 0 ; }