int main ( ) { int t , n , i , j , x , sum , k , max ; scanf ( "%d" , & t ) ; for ( i = 0 ; i < t ; i ++ ) { sum = 0 ; scanf ( "%d" , & n ) ; int a [ n ] , b [ n - 1 ] ; for ( j = 0 ; j < n ; j ++ ) { scanf ( "%d" , & a [ j ] ) ; } if ( n == 2 ) { if ( a [ 0 ] > a [ 1 ] ) { printf ( "%d\n" , a [ 1 ] ) ; continue ; } else { printf ( "%d\n" , a [ 0 ] ) ; continue ; } } for ( k = 0 ; k < n - 1 ; k ++ ) { for ( j = 0 ; j < n - k - 1 ; j ++ ) { b [ j ] = fabs ( a [ j ] - a [ j + 1 ] ) ; } max = b [ 0 ] ; for ( j = 1 ; j < n - k - 1 ; j ++ ) { if ( b [ j ] > max ) { max = b [ j ] ; x = j ; } } if ( a [ x ] > a [ x + 1 ] ) { sum = sum + a [ x + 1 ] ; for ( j = x ; j < n - k - 2 ; j ++ ) { a [ j ] = a [ j + 1 ] ; } } else { sum = sum + a [ x ] ; for ( j = x + 1 ; j < n - k - 2 ; j ++ ) { a [ j ] = a [ j + 1 ] ; } } } printf ( "%d\n" , sum ) ; } return 0 ; }