long long int gcd ( long long int a , long long int b ) { long long int z ; while ( a % b ) { z = b ; b = a % b ; a = z ; } return b ; } long long int lcm ( long long int a , long long int b ) { return a * b / gcd ( a , b ) ; } int main ( void ) { int t , i ; long long int a , b ; scanf ( "%d" , & t ) ; for ( i = 0 ; i < t ; i ++ ) { scanf ( "%lld %lld" , & a , & b ) ; if ( a > b ) printf ( "%lld %lld\n" , gcd ( a , b ) , lcm ( a , b ) ) ; else printf ( "%lld %lld\n" , gcd ( b , a ) , lcm ( b , a ) ) ; } return 0 ; }