int gcd ( int a , int b ) { if ( b == 0 ) { return a ; } return gcd ( b , a % b ) ; } int lcm ( int a , int b ) { return ( a * b ) / gcd ( a , b ) ; } int main ( void ) { long long test , a , b ; scanf ( "%lld" , & test ) ; while ( test -- ) { scanf ( "%lld%lld" , & a , & b ) ; long long m = gcd ( a , b ) ; long long l = lcm ( a , b ) ; printf ( "%lld %lld\n" , m , l ) ; } }