int main ( ) { int a , b , t , i , g , l , r , n , d ; scanf ( "%d" , & t ) ; for ( i = 1 ; i <= t ; i ++ ) { scanf ( "%d%d" , & a , & b ) ; if ( a > b ) { n = a ; d = b ; } else { n = b ; d = a ; } r = n % d ; while ( r != 0 ) { n = d ; d = r ; r = n % d ; } g = d ; l = a * b / g ; printf ( "%d %d\n" , g , l ) ; } return 0 ; }