int main ( void ) { int t ; scanf ( "%d" , & t ) ; while ( t -- ) { int n ; scanf ( "%d" , & n ) ; int arr [ n + 2 ] ; int i ; for ( i = 0 ; i < n ; i ++ ) { scanf ( "%d" , & arr [ i ] ) ; } int min = arr [ 0 ] ; for ( i = 0 ; i < n ; i ++ ) { if ( arr [ i ] < min ) min = arr [ i ] ; } long int ans = min * ( n - 1 ) ; printf ( "%ld\n" , ans ) ; } }