int main ( ) { int gcd ( int x , int y ) ; int lcm ( int p , int q ) ; int n , f , s , i ; scanf ( "%d" , & n ) ; for ( i = 0 ; i < n ; i ++ ) { scanf ( "%d %d" , & f , & s ) ; printf ( "%d %d\n" , gcd ( f , s ) , lcm ( f , s ) ) ; } return 0 ; } int gcd ( int x , int y ) { int z ; if ( x > y ) { while ( z != 0 ) { z = x % y ; if ( z == 0 ) { return y ; } x = y ; y = z ; } } else { while ( z != 0 ) { z = y % x ; if ( z == 0 ) { return x ; } y = x ; x = z ; } } } int lcm ( int p , int q ) { int i ; if ( p > q ) { for ( i = 2 ; i <= q ; i ++ ) { if ( q % i == 0 && p % i == 0 ) { return i ; break ; } } } else { for ( i = 2 ; i <= p ; i ++ ) { if ( q % i == 0 && p % i == 0 ) { return i ; break ; } } } return ( p * q ) ; }