int main ( ) { int test_case ; long long int n , count , sum , i ; scanf ( "%d" , & test_case ) ; while ( test_case -- ) { scanf ( "%lld" , & n ) ; long long int a [ n ] ; count = 1 ; sum = 0 ; for ( i = 0 ; i < n ; i ++ ) { scanf ( "%lld" , & a [ i ] ) ; } for ( i = 1 ; i < n ; i ++ ) { if ( a [ i ] >= a [ i - 1 ] ) { count ++ ; } else { sum = sum + ( count * ( count + 1 ) / 2 ) ; count = 1 ; } } sum = sum + ( count * ( count + 1 ) / 2 ) ; printf ( "%lld\n" , sum ) ; } return 0 ; }