int main ( void ) { int test ; long int n , cnt , i , a [ 50000 ] ; scanf ( "%d" , & test ) ; long int min ; long long ans ; while ( test -- ) { min = 100001 ; cnt = 0 ; scanf ( "%ld" , & n ) ; for ( i = 0 ; i < n ; i ++ ) { scanf ( "%ld" , & a [ i ] ) ; if ( a [ i ] < min ) { cnt = 1 ; min = a [ i ] ; } else if ( a [ i ] == min ) { cnt ++ ; } } ans = ( n - cnt ) * min ; printf ( "%lld\n" , ans ) ; } return 0 ; }