int main ( void ) { int tc , a , b , i , hcf , lcm , min , j ; scanf ( "%d" , & tc ) ; for ( i = 0 ; i < tc ; i ++ ) { scanf ( "%d" , & a ) ; scanf ( "%d" , & b ) ; min = a > b ? b : a ; hcf = 1 ; for ( j = min ; j >= 2 ; j -- ) { if ( a % j == 0 && b % j == 0 && hcf % j != 0 ) hcf = hcf * j ; if ( ( j == min || ( a * b ) % j == 0 ) && a % j == 0 && b % j == 0 ) break ; } lcm = ( a * b ) / hcf ; printf ( "%d %d \n" , hcf , lcm ) ; } return 0 ; }