int main ( ) { int t ; scanf ( "%d" , & t ) ; int i , j ; for ( i = 0 ; i < t ; i ++ ) { int a , b ; int lcm , gcd ; scanf ( "%d %d" , & a , & b ) ; int mx = ( a > b ) ? a : b ; int mn = ( a > b ) ? b : a ; if ( mx % mn == 0 ) { lcm = mx ; } else { for ( j = 2 ; j <= mn ; j ++ ) { lcm = j * mx ; if ( lcm % mn == 0 ) break ; } } gcd = 1 ; for ( j = 1 ; j <= mn ; j ++ ) { if ( a % j == 0 && b % j == 0 ) gcd = j ; } printf ( "%d %d\n" , gcd , lcm ) ; } }