int main ( ) { int i , j , T , N , A [ 50000 ] , temp , count ; scanf ( "%d" , & T ) ; for ( j = 0 ; j < T ; j ++ ) { scanf ( "%d" , & N ) ; for ( i = 0 ; i < N ; i ++ ) scanf ( "%d" , & A [ i ] ) ; count = 1 ; for ( i = 1 ; i < N ; i ++ ) { if ( A [ i ] < A [ 0 ] ) { temp = A [ i ] ; A [ i ] = A [ 0 ] ; A [ 0 ] = temp ; count = 1 ; } else if ( A [ i ] == A [ 0 ] ) { count ++ ; } } printf ( "%d\n" , ( N - count ) * A [ 0 ] ) ; } return 0 ; }