int main ( ) { long long int t , i , num1 , num2 , j , multi ; scanf ( "%d" , & t ) ; double lc [ t ] , hc [ t ] ; for ( i = 0 ; i < t ; i ++ ) { scanf ( "%lld %lld" , & num1 , & num2 ) ; for ( j = 1 ; j > 0 ; j ++ ) { multi = num1 * j ; if ( multi % num2 == 0 ) break ; } lc [ i ] = multi ; hc [ i ] = num1 * num2 / multi ; } for ( i = 0 ; i < t ; i ++ ) printf ( "%0.0lf %0.0lf\n" , hc [ i ] , lc [ i ] ) ; return 0 ; }