int main ( ) { int t ; scanf ( "%d" , & t ) ; while ( t -- ) { int n , a [ 100000 ] , count = 0 , i ; long long int ans = 0 ; scanf ( "%d" , & n ) ; for ( i = 0 ; i < n ; i ++ ) scanf ( "%d" , & a [ i ] ) ; for ( i = 0 ; i < n - 1 ; i ++ ) { if ( a [ i ] <= a [ i + 1 ] ) count ++ ; else { ans += count * ( count + 1 ) / 2 ; count = 0 ; } } ans += count * ( count + 1 ) / 2 ; ans += n ; printf ( "%lld\n" , ans ) ; } return 0 ; }