main ( ) { int A , B , gcd , lcm , rem , T , nr , dr , i ; scanf ( "%d" , & T ) ; for ( i = 0 ; T != 0 ; i ++ ) { scanf ( "%ld%ld" , & A , & B ) ; if ( A > B ) { nr = A ; dr = B ; } else { nr = B ; dr = A ; } rem = nr % dr ; while ( rem != 0 ) { nr = dr ; dr = rem ; rem = nr % dr ; } gcd = dr ; lcm = A * B / gcd ; printf ( "%ld\t" , gcd ) ; printf ( "%ld\n" , lcm ) ; T -- ; } return 0 ; }