int main ( ) { int numTests = 0 , num1 = 10 , num2 = 3 , i = 1 , j = 0 , gcd = 1 , lcm = 1 ; static int gcds [ MAX ] , lcms [ MAX ] ; scanf ( "%d" , & numTests ) ; for ( j = 0 ; j < numTests ; j ++ ) { scanf ( "%d %d" , & num1 , & num2 ) ; while ( i <= num1 && i <= num2 ) { if ( num1 % i == 0 && num2 % i == 0 ) gcd = i ; i ++ ; } lcm = num1 * num2 / gcd ; gcds [ j ] = gcd ; lcms [ j ] = lcm ; } for ( i = 0 ; i < numTests ; i ++ ) printf ( "%d %d\n" , gcds [ i ] , lcms [ i ] ) ; }