unsigned int triangle_sums ( unsigned int num_lines ) { static unsigned int ar [ 100 ] ; unsigned int i , col , cur ; unsigned int max = 0 ; for ( i = 0 ; i < num_lines ; i ++ ) { ar [ i ] = 0 ; } for ( i = 1 ; i <= num_lines ; i ++ ) { for ( col = 0 ; col < i ; col ++ ) { scanf ( "%u" , & cur ) ; ar [ col ] += cur ; if ( ar [ col ] > max ) { max = ar [ col ] ; } } for ( col = i ; col > 0 ; col -- ) { if ( ar [ col - 1 ] > ar [ col ] ) { ar [ col ] = ar [ col - 1 ] ; } } } return max ; } int main ( ) { unsigned int num_test_cases ; unsigned int num_tri_lines ; if ( scanf ( "%u" , & num_test_cases ) != 1 ) { return 1 ; } for ( ; num_test_cases > 0 ; num_test_cases -- ) { if ( scanf ( "%u" , & num_tri_lines ) != 1 ) { return 1 ; } printf ( "%u\n" , triangle_sums ( num_tri_lines ) ) ; } return 0 ; }