void gcd ( long x , long y ) { int j ; long result = 1 ; for ( j = 2 ; j <= y ; j ++ ) { while ( x / j == 0 && y / j == 0 ) { result = result * j ; x %= j ; y %= j ; } } if ( x != y ) printf ( "%ld\t%ld\n" , result , result * x * y ) ; else printf ( "%ld\t%ld\n" , result , result ) ; } int main ( ) { int t , i ; long a , b ; scanf ( "%d" , & t ) ; for ( i = 0 ; i < t ; i ++ ) { scanf ( "%ld%ld" , & a , & b ) ; gcd ( a , b ) ; } return 0 ; }