int gcd ( int a , int b ) { if ( a == 0 || b == 0 ) { return 0 ; } if ( a == b ) { return a ; } if ( a > b ) { return gcd ( a - b , b ) ; } return gcd ( a , b - a ) ; } int main ( void ) { long long int a , b ; int t ; long long int g ; scanf ( "%d" , & t ) ; while ( t -- ) { scanf ( "%lld%lld" , & a , & b ) ; g = gcd ( a , b ) ; printf ( "%lld" , g ) ; printf ( " %lld" , ( a * b ) / ( g ) ) ; printf ( "\n" ) ; } return 0 ; }