int main ( ) { int t , n , arr [ 100005 ] , i , j , max , x ; long long sum ; scanf ( "%d" , & t ) ; while ( t -- ) { scanf ( "%d" , & n ) ; for ( i = 1 ; i <= n ; i ++ ) scanf ( "%d" , & arr [ i ] ) ; sum = 1 ; max = arr [ 1 ] ; x = 1 ; for ( i = 2 ; i <= n ; i ++ ) { if ( arr [ i ] >= max ) { sum += x + 1 ; x ++ ; max = arr [ i ] ; } else { x = 1 ; max = arr [ i ] ; sum += 1 ; } } printf ( "%lld\n" , sum ) ; } return 0 ; }