int main ( int argc , char * * argv ) { int prev_row [ MAX_ITEMS ] ; int problem_count ; scanf ( "%d" , & problem_count ) ; getchar ( ) ; for ( ; problem_count != 0 ; problem_count -- ) { int row_count ; scanf ( "%d" , & row_count ) ; getchar ( ) ; int row_number ; for ( row_number = 0 ; row_number < row_count ; row_number ++ ) { prev_row [ row_number ] = 0 ; int prev_left ; { int item ; scanf ( "%d" , & item ) ; getchar ( ) ; prev_left = prev_row [ 0 ] ; prev_row [ 0 ] = prev_left + item ; } int item_number ; for ( item_number = 1 ; item_number <= row_number ; item_number ++ ) { int item ; scanf ( "%d" , & item ) ; getchar ( ) ; int prev_right = prev_row [ item_number ] ; prev_row [ item_number ] = ( prev_left > prev_right ? prev_left : prev_right ) + item ; prev_left = prev_right ; } } int item_number ; int max = 0 ; for ( item_number = 0 ; item_number != row_count ; item_number ++ ) { int item = prev_row [ item_number ] ; if ( item > max ) max = item ; } printf ( "%d\n" , max ) ; } return 0 ; }