int main ( ) { int n , i , y , x , j , lcm = 0 , hcf = 0 ; scanf ( "%d" , & n ) ; for ( i = 0 ; i <= n - 1 ; i ++ ) { scanf ( "%d" , & x ) ; scanf ( "%d" , & y ) ; hcf = gcd ( x , y ) ; lcm = ( x * y ) / hcf ; printf ( "%d %d\n" , hcf , lcm ) ; } return 0 ; } int gcd ( int a , int b ) { if ( a == 0 || b == 0 ) return 0 ; if ( a == b ) return a ; if ( a > b ) return gcd ( a - b , b ) ; else return gcd ( a , b - a ) ; }