int main ( ) { int n ; scanf ( "%d" , & n ) ; while ( n -- ) { int s ; scanf ( "%d" , & s ) ; int a [ s ] ; for ( int i = 1 ; i <= s ; i ++ ) scanf ( "%d" , & a [ i ] ) ; for ( int i = 1 ; i <= s ; i ++ ) { for ( int j = i + 1 ; j <= s ; j ++ ) { if ( a [ i ] > a [ j ] ) { int t = a [ i ] ; a [ i ] = a [ j ] ; a [ j ] = t ; } } } if ( s == 1 ) printf ( "%d\n" , a [ 1 ] ) ; else { int a1 = a [ 1 ] ; int sum = 0 , b ; for ( int i = 2 ; i <= s ; i ++ ) { b = a [ i ] ; if ( a1 > b ) { sum = sum + b ; a1 = b ; } else sum = sum + a1 ; } printf ( "%d\n" , sum ) ; } } }