int sot ( int * arr , int i , int j , int n ) { if ( i >= n ) return 0 ; int x = sot ( arr , i + 1 , j , n ) ; int y = sot ( arr , i + 1 , j + 1 , n ) ; int m = ( x > y ? x : y ) ; return ( * ( arr + i * n + j ) + m ) ; } int main ( ) { int t ; scanf ( "%d" , & t ) ; while ( t -- ) { int n , i , j ; scanf ( "%d" , & n ) ; int arr [ n ] [ n ] ; for ( i = 0 ; i < n ; i ++ ) for ( j = 0 ; j <= i ; j ++ ) scanf ( "%d" , & arr [ i ] [ j ] ) ; int sum = sot ( ( int * ) arr , 0 , 0 , n ) ; printf ( "%d\n" , sum ) ; } return 0 ; }