int main ( ) { int t , n1 , n2 , i , j , k , temp ; scanf ( "%d" , & t ) ; int g [ t ] , l [ t ] ; for ( i = 0 ; i < t ; i ++ ) { scanf ( "%d%d" , & n1 , & n2 ) ; if ( n1 == n2 ) { g [ i ] = n1 ; l [ i ] = n1 ; continue ; } if ( n1 < n2 ) { temp = n1 ; n1 = n2 ; n2 = temp ; } for ( j = 1 ; j <= n1 ; j ++ ) { if ( n1 % j == 0 && n2 % j == 0 ) g [ i ] = j ; l [ i ] = n1 * n2 / g [ i ] ; } } for ( i = 0 ; i < t ; i ++ ) printf ( "%d %d\n" , g [ i ] , l [ i ] ) ; return 0 ; }