int main ( void ) { int t , i ; scanf ( "%d" , & t ) ; for ( i = 0 ; i < t ; i ++ ) { long int n , j ; long long int num [ 100001 ] ; scanf ( "%ld" , & n ) ; for ( j = 1 ; j <= n ; j ++ ) scanf ( "%lld" , & num [ j ] ) ; long long int answer = 0 ; long long int start = 1 ; long long int end = 1 ; j = 1 ; while ( j <= n ) { while ( ( num [ j ] <= num [ j + 1 ] ) && ( j < n ) ) { j ++ ; } end = j ; answer = answer + ( end - start + 1 ) * ( end - start + 2 ) / 2 ; j ++ ; start = j ; end = j ; } printf ( "%lld\n" , answer ) ; } return 0 ; }