int main ( void ) { int t ; scanf ( "%d" , & t ) ; for ( int tc = 0 ; tc < t ; tc ++ ) { long long int n ; scanf ( "%lld" , & n ) ; long long int a [ n ] ; for ( long long int i = 0 ; i < n ; i ++ ) { scanf ( "%lld" , & a [ i ] ) ; } int min = a [ 0 ] ; for ( long long int i = 1 ; i < n ; i ++ ) { if ( min > a [ i ] ) min = a [ i ] ; } printf ( "%lld\n" , min * ( n - 1 ) ) ; } return 0 ; }