int main ( ) { int A [ 50000 ] , n , i , count = 1 , t ; unsigned long int cost = 0 ; scanf ( "%d" , & t ) ; while ( count <= t ) { cost = 0 ; scanf ( "%d\b" , & n ) ; for ( i = 0 ; i < n ; i ++ ) scanf ( "%d" , A + i ) ; i = 0 ; while ( i < n - 1 ) { if ( A [ i ] > A [ i + 1 ] ) { cost += A [ i + 1 ] ; i ++ ; } else { cost += A [ i ] ; A [ i + 1 ] = A [ i ] ; i ++ ; } } printf ( "%lu\n" , cost ) ; count ++ ; } return 0 ; }