int max ( int a , int b ) { if ( a > b ) { return a ; } else { return b ; } } int min ( int x , int y ) { if ( x < y ) return x ; else return y ; } int main ( void ) { int i , t , gcd , lcm , m , r ; long a , b , x , y ; scanf ( "%d" , & t ) ; for ( i = 0 ; i < t ; i ++ ) { scanf ( "%d%d" , & a , & b ) ; x = a , y = b ; x = max ( a , b ) ; m = min ( a , b ) ; r = x % y ; while ( x % y ) { x = m , y = r ; if ( x % y == 0 ) { gcd = y ; break ; } else r = x % y ; m = min ( x , y ) ; } lcm = ( a * b ) / gcd ; printf ( "%d %d\n" , gcd , lcm ) ; } return 0 ; }