int main ( ) { int t ; scanf ( "%d" , & t ) ; while ( t -- ) { int n , i , min ; scanf ( "%d" , & n ) ; int arr [ n ] ; for ( i = 0 ; i < n ; i ++ ) { scanf ( "%d" , & arr [ i ] ) ; } min = arr [ 0 ] ; for ( i = 0 ; i < n ; i ++ ) { if ( arr [ i ] <= min ) { min = arr [ i ] ; } } printf ( "%lld\n" , min * ( n - 1 ) ) ; } return 0 ; }