int gcd ( int a , int b ) ; int main ( int argc , const char * argv [ ] ) { int numOfCases ; int i , n1 , n2 ; scanf ( "%d" , & numOfCases ) ; for ( i = 0 ; i < numOfCases ; i ++ ) { scanf ( "%d %d" , & n1 , & n2 ) ; int temp = gcd ( n1 , n2 ) ; printf ( " my : %d %d\n" , temp , ( n1 * n2 ) / temp ) ; } return 0 ; } int gcd ( int a , int b ) { int t ; if ( a < b ) { t = a ; a = b ; b = t ; } while ( b != 0 ) { t = b ; b = a % b ; a = t ; } return a ; }