int main ( void ) { int t ; scanf ( "%d" , & t ) ; while ( t -- ) { int a , i , j , cost = 0 , min ; scanf ( "%d" , & a ) ; int arr [ a ] ; for ( i = 0 ; i < a ; i ++ ) { scanf ( "%d" , & arr [ i ] ) ; } for ( j = 0 ; j < a - 1 ; j ++ ) { if ( arr [ j ] < arr [ j + 1 ] ) min = arr [ j ] ; else min = arr [ j + 1 ] ; } cost = min * ( a - 1 ) ; printf ( "%d\n" , cost ) ; } return 0 ; }