int gcd ( int , int ) ; int main ( ) { int t , i ; scanf ( "%d" , & t ) ; for ( i = 0 ; i < t ; i ++ ) { int a , b , n , lcm ; scanf ( "%d%d" , & a , & b ) ; n = gcd ( a , b ) ; lcm = a * b / n ; printf ( "%d " , n ) ; printf ( "%d\n" , lcm ) ; } } int gcd ( int x , int y ) { if ( x == 0 ) return y ; else if ( y == 0 ) return x ; else if ( x >= y ) { if ( x % y == 0 ) return y ; else return gcd ( y , x % y ) ; } else { if ( y % x == 0 ) return x ; else return ( x , y % x ) ; } }