int main ( ) { short int t ; int i , j ; scanf ( "%hu" , & t ) ; while ( t -- ) { unsigned long n , temp ; scanf ( "%lu" , & n ) ; unsigned long arr [ n ] ; for ( j = 0 ; j < n ; j ++ ) scanf ( "%lu" , & arr [ j ] ) ; for ( i = 0 ; i < n ; i ++ ) { for ( j = 0 ; j < n ; j ++ ) { if ( j + 1 < n && arr [ j ] > arr [ j + 1 ] ) { temp = arr [ j + 1 ] ; arr [ j + 1 ] = arr [ j ] ; arr [ j ] = temp ; } } } printf ( "%lu\n" , arr [ 0 ] * ( n - 1 ) ) ; } return 0 ; }