int main ( ) { int a ; scanf ( "%d" , & a ) ; int count = 0 ; for ( int i = 0 ; i < a ; i ++ ) { int n ; scanf ( "%d" , & n ) ; int lenlist [ 102 ] ; for ( int j = 0 ; j < n + 1 ; j ++ ) { count = 0 ; for ( int k = 0 ; k < j ; k ++ ) { int var ; scanf ( "%d" , & var ) ; lenlist [ k ] += var ; } } count = 0 ; for ( int j = 0 ; j < n ; j ++ ) { if ( lenlist [ j ] > count ) { count = lenlist [ j ] ; } } for ( int j = 0 ; j < 102 ; j ++ ) { lenlist [ j ] = 0 ; } printf ( "%d\n" , count + 1 ) ; count = 0 ; } return 0 ; }