int gcd ( int , int ) ; int main ( ) { int x , y , g , lcm , t , i ; scanf ( "%d" , & t ) ; for ( i = 0 ; i < t ; i ++ ) { scanf ( "%d%d" , & x , & y ) ; g = gcd ( x , y ) ; lcm = ( x * y ) / g ; printf ( "%d %d" , g , lcm ) ; } return 0 ; } int gcd ( int a , int b ) { if ( a > b ) { if ( a % b == 0 ) return b ; else return gcd ( b , a % b ) ; } else if ( b % a == 0 ) return a ; else return gcd ( a , b % a ) ; }