long minimum ( long a , long b ) { if ( a > b ) return b ; else return a ; } int main ( ) { int n ; long a , b , min , hcf , lcm ; scanf ( "%d" , n ) ; while ( n -- ) { scanf ( "%ld%ld" , & a , & b ) ; min = minimum ( a , b ) ; int i ; for ( i = 1 ; i <= min ; i ++ ) { if ( a % i == 0 && b % i == 0 ) hcf = i ; } lcm = a * b / hcf ; printf ( "%ld %ld\n" , hcf , lcm ) ; } return 0 ; }