int main ( ) { int a , b , rem , num , deno , lcm , gcd ; printf ( "Enter two numbers" ) ; scanf ( "%d %d" , & a , & b ) ; if ( a > b ) { num = a ; deno = b ; } else { num = b ; deno = a ; } rem = num % deno ; while ( rem != 0 ) { num = deno ; deno = rem ; rem = num % deno ; } gcd = deno ; lcm = ( a * b ) / gcd ; printf ( "\n gcd and lcm of two numbers are %d and %d " , gcd , lcm ) ; return 0 ; }