int main ( ) { int t , i ; long long a , b , gcd , lcm , j , l , s , p ; scanf ( "%d" , & t ) ; for ( i = 1 ; i <= t ; i ++ ) { scanf ( "%lld%lld" , & a , & b ) ; if ( a >= b ) { l = a ; s = b ; } else { l = b ; s = a ; } for ( j = s ; j >= 1 ; ) { if ( l % j == 0 && s % j == 0 ) { gcd = j ; break ; } else if ( s % j != 0 ) j = j - s % j ; else if ( l % j != 0 ) j = j - l % j ; } lcm = ( a * b ) / gcd ; printf ( "%lld %lld\n" , gcd , lcm ) ; } return 0 ; }