int gcd ( int , int ) ; int lcm ( int , int ) ; int main ( ) { int t = 0 , a = 0 , b = 0 ; scanf ( "%d" , & t ) ; while ( t -- > 0 ) { scanf ( "%d %d" , & a , & b ) ; int g = gcd ( a , b ) ; int l = lcm ( a , b ) ; printf ( "%d %d\n" , g , l ) ; } return 0 ; } int gcd ( int x , int y ) { int dividend = 0 , divisor = 0 , rem = 0 ; if ( x > y ) { dividend = x ; divisor = y ; } else { dividend = y ; divisor = x ; } while ( divisor ) { rem = dividend % divisor ; dividend = divisor ; divisor = rem ; } return dividend ; } int lcm ( int x , int y ) { int min = 0 , max = 0 , lc = 0 , a = 1 , i = 0 ; if ( x < y ) { min = x ; max = y ; } else { min = y ; max = x ; } for ( i = 1 ; i <= min ; i ++ ) { a = max * i ; if ( a % min == 0 ) { lc = a ; break ; } } return lc ; }